  \begin{eqnarray*}
   \max \{ u(t,x) : t\in [0,T], x\in U\} &\leq & \max\{v_{\varepsilon}(t,x) : t\in [0,T], x\in U \} + \varepsilon T\\
   		& = & \max\{v_{\varepsilon}(t,x) : (t,x)\in \partial_P([0,T]\times U)  \} + \varepsilon T\\
		& \leq &\max\{ u(t,x) : (t,x)\in \partial_P([0,T]\times U)  \} + \varepsilon T\\
		& \to & \max\{ u(t,x) : (t,x)\in \partial_P([0,T]\times U)  \} \qquad (\varepsilon \to 0).
  \end{eqnarray*}

 \begin{eqnarray*}
   u_k'(t,x)  &=& -\Delta_{U_k} u_k(t,x)\\
   		&=& \left\{\begin{array}{ll} 
			-m(x)u_k(t,x) + \sum_{y\sim x} u_k(t,y), & \mbox{if~} x\in\mathring{U}_k\\
			0, & \mbox{else}
			\end{array}\right.\\
		&\to& -m(x)u(t,x) + \sum_{y\sim x} u(t,y)  = -\Delta u(t,x),
 \end{eqnarray*}

\begin{eqnarray*}
 u_k(t,x)  &\leq &  \sup_{y\in V} u_0(y)\sum_{y\in V} p_k(t,x,y)\\
 			&\leq& \sup_{y\in V} u_0(y) 
\end{eqnarray*}

  \begin{eqnarray*}
   \lim_{t\to 0^+} \big(u(t,x) - u_0(x)\big)  &=&
   		\lim_{t\to 0^+} \sum_{y\in V} p(t,x,y) \big(u_0(y) - u_0(x)\big)\\
		&=& \lim_{t\to 0^+} \sum_{y\neq x} p(t,x,y) \big(u_0(y) - u_0(x)\big).		 
  \end{eqnarray*}

 \begin{eqnarray*}
  ||P_t u_0||^2_{\ell^2(V)}  &=& \sum_{x\in V}\Big| \sum_{y\in V} p(t,x,y)u_0(y)  \Big|^2\\
  		&\leq& \lim_{k\to\infty}  \sum_{x\in V}\Big| \sum_{y\in V} p_k(t,x,y)u_0(y)  \Big|^2\\
		&=& \lim_{k\to\infty}  ||\e^{-t\Delta_{U_k}}u_0||^2_{\ell^2(U_k)}\\
		&\leq& ||u_0||^2_{\ell^2(V)},
 \end{eqnarray*}
